import React, { useState, useEffect } from 'react';
import "./users.scss";

interface User {
    id: number;
    username: string;
    email: string;
    createdAt: Date;
    updatedAt: Date;
    verified: boolean;
    age: number;
}

const Users = () => {
    const [users, setUsers] = useState<User[]>([]);
    const [newUser, setNewUser] = useState<User>({
        id: 0,
        username: '',
        email: '',
        createdAt: new Date(),
        updatedAt: new Date(),
        verified: false,
        age: 0
    });

    const addUser = () => {
        // Implement the addUser logic here, similar to Angular
        // You may want to use the useState hook to update newUser
    };

    useEffect(() => {
        // Implement the getUsers logic here, similar to Angular
        // Use the setUsers function to update the user list
    }, []);

    return (
        <div>
            <form onSubmit={addUser} className="user-form-container">
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={newUser.username}
                        onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={newUser.email}
                        onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="age">Age</label>
                    <input
                        type="number"
                        id="age"
                        name="age"
                        value={newUser.age}
                        onChange={(e) => setNewUser({ ...newUser, age: Number(e.target.value) })}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="verified">Verified</label>
                    <input
                        type="checkbox"
                        id="verified"
                        name="verified"
                        checked={newUser.verified}
                        onChange={(e) => setNewUser({ ...newUser, verified: e.target.checked })}
                    />
                </div>
                <button type="submit">Add User</button>
            </form>
            <div className="list">
                {users.map((user) => (
                    <div className="list__item" key={user.id}>
                        <div className="left-content">
                            <div className="id">{user.id}</div>
                            <div className="name">{user.username}</div>
                            <div className="mail">{user.email}</div>
                        </div>
                        <div className="right-content">
                            <div className="date">{user.createdAt.toISOString()}</div>
                            <div className="date">{user.updatedAt.toISOString()}</div>
                            <div className="age">{user.age}</div>
                            <input type="checkbox" checked={user.verified} />
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Users;
